$(document).ready(function(){$('.container-button button').click(function(){$('.container-button').addClass('active');});$('.container-button .box_bottom .close').click(function(){$('.container-button').removeClass('active');});$(".downloadLink").click(function(e){e.preventDefault();var href=$(this).attr("href");window.open($(this).attr("data-download"));window.location=href;window.focus();});if($(window).width()>=8){var nav_shown=false;var NAV_SCROLL_THRESHOLD=10;var white_nav=$('nav.navtop');var showNav=function(){nav_shown=true;white_nav.show();white_nav.removeClass('hideBar').addClass('show');};var hideNav=function(){nav_shown=false;white_nav.hide();white_nav.removeClass('show').addClass('hideBar');};document.onscroll=function(e){var scrollTop=$(window).scrollTop();if(!nav_shown){if(scrollTop>NAV_SCROLL_THRESHOLD){showNav();}}else{if(scrollTop<NAV_SCROLL_THRESHOLD){hideNav();}}};}
$('.carousel').carousel({interval:9000,pause:'hover',keyboard:true})
$('.carousel').on('slide.bs.carousel',function(){$('#hero').toggleClass('second');})
$("#download").click(function(e){e.preventDefault();var href=$(this).attr("href");window.location=href;});});$(document).on('click','.js-videoPoster',function(ev){ev.preventDefault();var $poster=$(this);var $wrapper=$poster.closest('.js-videoWrapper');videoPlay($wrapper);});function videoPlay($wrapper){var $iframe=$wrapper.find('.js-videoIframe');var src=$iframe.data('src');$wrapper.addClass('videoWrapperActive');$iframe.attr('src',src);}
function videoStop($wrapper){if(!$wrapper){var $wrapper=$('.js-videoWrapper');var $iframe=$('.js-videoIframe');}else{var $iframe=$wrapper.find('.js-videoIframe');}
$wrapper.removeClass('videoWrapperActive');$iframe.attr('src','');};